import React, { useState } from "react";
import "../css/Contact.css";

const ContactPage = () => {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({
    customerName: "",
    date: "",
    machine: "",
    beltType: "",
    temperature: "",
    // Add other fields as needed
  });

  const nextStep = () => {
    setStep(step + 1);
  };

  const prevStep = () => {
    setStep(step - 1);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Add your submit logic here
    console.log("Form submitted:", formData);
  };
  const getContainerStyle = () => {
    // Adjust the styles as needed based on your layout
    return {
      width: "80%",
      margin: "auto",
      padding: "20px",
      border: "1px solid #ddd",
      borderRadius: "10px",
      boxShadow: "0 0 10px rgba(0, 0, 0, 0.1)",
    };
  };
  return (
    <div style={getContainerStyle()}>

        <form className="form" onSubmit={handleSubmit}>
          {step === 1 && (
            <div>
              <h2>Step 1: Customer Details</h2>
              <label>
                Customer Name:
                <input
                  type="text"
                  name="customerName"
                  value={formData.customerName}
                  onChange={handleChange}
                />
              </label>
              <br />
              <label>
                Date:
                <input
                  type="date"
                  name="date"
                  value={formData.date}
                  onChange={handleChange}
                />
              </label>
              <br />
              <button type="button" onClick={nextStep}>
                Next
              </button>
            </div>
          )}

          {step === 2 && (
            <div>
              <h2>Step 2: Technical Details</h2>
              {/* Add technical details fields */}
              <button type="button" onClick={prevStep}>
                Previous
              </button>
              <button type="button" onClick={nextStep}>
                Next
              </button>
            </div>
          )}

          {step === 3 && (
            <div>
              <h2>Step 3: Conveyor System Details</h2>
              {/* Add conveyor system details fields */}
              <button type="button" onClick={prevStep}>
                Previous
              </button>
              <button type="button" onClick={nextStep}>
                Next
              </button>
            </div>
          )}

          {step === 4 && (
            <div>
              <h2>Preview</h2>
              {/* Display a preview of the entered data */}
              <pre>{JSON.stringify(formData, null, 2)}</pre>
              <button type="button" onClick={prevStep}>
                Previous
              </button>
              <button type="submit">Submit</button>
            </div>
          )}
        </form>

    </div>
  );
};

export default ContactPage;
