import React, { useState } from "react";
import Home from './components/HomePage';
import Product from './components/ProductPage';
import About from './components/AboutUs';
import ContactPage from './components/Contact';

const Admin = () => {
  const [activeSection, setActiveSection] = useState('home');
  const buttonStyle = {
    flex: 1,
    border: 'none',
    background: 'none',
    cursor: 'pointer',
    padding: '0',
    fontSize: '16px', // You can adjust the font size as needed
    color: 'black',
    marginRight:'40px', // Set the text color
  };

  return (
    <div className="admin-container">
      <div className="menu-container" style={{ backgroundColor: 'rgb(173, 37, 7)', height: '50px', display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
        <div className="left-menu" style={{ flex: 1, display: 'flex', alignItems: 'center' }}>
          <div className="logo">
          <img src="./assets/logo.jpeg" alt="logo"/>

          </div>
        </div>
        <div className="right-menu" style={{ flex: 1, display: 'flex', justifyContent: 'flex-end' }}>
          <nav className="sidebar">
            <ul className="nav-links" style={{ display: 'flex', listStyle: 'none', padding: '0' }}>
              <button style={buttonStyle} onClick={() => setActiveSection('home')}>Home</button>
              <button style={buttonStyle} onClick={() => setActiveSection('products')}>Products</button>
              <button style={buttonStyle} onClick={() => setActiveSection('contact')}>Contact</button>
              <button style={buttonStyle} onClick={() => setActiveSection('aboutus')}>AboutUs</button>
            </ul>
          </nav>
        </div>
      </div>
      <div className="admin-dashboard">
        <header className="dashboard-header">
          {/* Add any header content here */}
        </header>
        {activeSection === 'home' && <Home />}
        {activeSection === 'products' && <Product />}
        {activeSection === 'contact' && <ContactPage />}
        {activeSection === 'aboutus' && <About />}
      </div>
    </div>
  );
};

export default Admin;
