import { Swiper, SwiperSlide } from "swiper/react";
import { Autoplay, Pagination } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/pagination';
import './Slider.css';
import React, { useRef } from 'react';

export const Slider = ({ slides }) => {
  const imageStyle = {
    width: "75%", // Ensure the image takes the full width of its container
  };

  const progressCircle = useRef(null);

  const onAutoplayTimeLeft = (s, time, progress) => {
    progressCircle.current.style.setProperty('--progress', 1 - progress);
  };

  return (
    <Swiper
      spaceBetween={30}
      centeredSlides={true}
      autoplay={{
        delay: 5000,
        disableOnInteraction: false,
      }}
      pagination={{
        clickable: true,
      }}
      navigation={false} // Set navigation to false to hide the arrows
      modules={[Autoplay, Pagination]}
      onAutoplayTimeLeft={onAutoplayTimeLeft}
      className="mySwiper"
    >
      {slides.map((slide) => (
        <SwiperSlide key={slide.image}>
          <div className="slide-card">
            <img src={slide.image} alt={slide.title} style={imageStyle} />
            {/* You can customize the card content as needed */}
            <div className="card-content">
              <h3>{slide.title}</h3>
              <p>{slide.description}</p>
            </div>
            <div className="autoplay-progress">
              <svg viewBox="0 0 48 48" ref={progressCircle}>
                <circle cx="24" cy="24" r="20"></circle>
              </svg>
            </div>
          </div>
        </SwiperSlide>
      ))}
    </Swiper>
  );
};
